select tm.id id, tm.time, f.name fieldName, tm.name typeName, tm.host_team_label hostTeamLabel, tm.host_team_id hostTeamId, (select name from team where id = tm.host_team_id) hostTeamName, (select logo from team where id = tm.host_team_id) hostTeamLogo, tm.guest_team_label guestTeamLabel, tm.guest_team_id guestTeamId, (select name from team where id = tm.guest_team_id) guestTeamName, (select logo from team where id = tm.guest_team_id) guestTeamLogo, (select concat(goals+(select own_goals from match_team_result mtc where tm.id=mtc.tournament_match_id and mtc.team_id= tm.guest_team_id),':',conceded) from match_team_result mtc where tm.id=mtc.tournament_match_id and mtc.team_id= tm.host_team_id) result, tm.video_url videoUrl from tournament_match tm left join field f on f.id = tm.field_id join tournament tn on tn.id = tm.tournament_id where tn.id = #{tournamentId} and tm.host_team_id = #{teamId}
union
select tm.id id, tm.time, f.name fieldName, tm.name typeName, tm.host_team_label hostTeamLabel, tm.host_team_id hostTeamId, (select name from team where id = tm.host_team_id) hostTeamName, (select logo from team where id = tm.host_team_id) hostTeamLogo, tm.guest_team_label guestTeamLabel, tm.guest_team_id guestTeamId, (select name from team where id = tm.guest_team_id) guestTeamName, (select logo from team where id = tm.guest_team_id) guestTeamLogo, (select concat(goals+(select own_goals from match_team_result mtc where tm.id=mtc.tournament_match_id and mtc.team_id= tm.guest_team_id),':',conceded) from match_team_result mtc where tm.id=mtc.tournament_match_id and mtc.team_id= tm.host_team_id) result, tm.video_url videoUrl from tournament_match tm left join field f on f.id = tm.field_id join tournament tn on tn.id = tm.tournament_id where tn.id = #{tournamentId} and tm.guest_team_id = #{teamId}

select tm.id id, tm.time, f.name fieldName, tm.name typeName, tm.host_team_label hostTeamLabel, tm.host_team_id hostTeamId, (select name from team where id = tm.host_team_id) hostTeamName, (select logo from team where id = tm.host_team_id) hostTeamLogo, tm.guest_team_label guestTeamLabel, tm.guest_team_id guestTeamId, (select name from team where id = tm.guest_team_id) guestTeamName, (select logo from team where id = tm.guest_team_id) guestTeamLogo, (select concat(pts_for,':',pts_against) from basketball_match_team_result bmtc where tm.id=bmtc.tournament_match_id and bmtc.team_id= tm.host_team_id) result from tournament_match tm join field f on f.id = tm.field_id join tournament tn on tn.id = tm.tournament_id where tn.id = #{tournamentId} and tm.host_team_id = #{teamId}
union
select tm.id id, tm.time, f.name fieldName, tm.name typeName, tm.host_team_label hostTeamLabel, tm.host_team_id hostTeamId, (select name from team where id = tm.host_team_id) hostTeamName, (select logo from team where id = tm.host_team_id) hostTeamLogo, tm.guest_team_label guestTeamLabel, tm.guest_team_id guestTeamId, (select name from team where id = tm.guest_team_id) guestTeamName, (select logo from team where id = tm.guest_team_id) guestTeamLogo, (select concat(pts_for,':',pts_against) from basketball_match_team_result bmtc where tm.id=bmtc.tournament_match_id and bmtc.team_id= tm.host_team_id) resul from tournament_match tm join field f on f.id = tm.field_id join tournament tn on tn.id = tm.tournament_id where tn.id = #{tournamentId} and tm.guest_team_id = #{teamId}

select tm.name matchName, tm.time matchTime, t.id teamId, t.name teamName, t.logo teamLogo, t.abbreviation teamAbbr, points, goals, conceded, shots, pass_finalball passFinalBall, assists, key_defense keyDefense, fouls, yellow_card yellowCard, red_card redCard, save, own_goals ownGoals from match_team_result mtr join tournament_match tm on tm.id = mtr.tournament_match_id and mtr.team_id = tm.host_team_id join team t on t.id = tm.guest_team_id where tm.tournament_id = #{tournamentId} and tm.host_team_id =#{teamId}
