union
(select fp.id playerId, fp.nick_name playerName, fp.imageUrl playerImg, null number, null position ,pp.birth birth, pp.height height, pp.weight weight, tpi.status status from football_player fp left join player_property pp on pp.id = fp.id join team_player_invitation tpi on tpi.player_id = fp.id and status != 'ACCEPT' where tpi.team_id = #{teamId} order by tpi.request_time asc )

