select fp.id playerId, fp.nick_name playerName, fp.imageUrl playerLogo, s.date eventDate, 'forward' eventAction, 'video' eventType, v.type eventDetailType, if(v.type=0, v.tournament_match_id, v.id) eventId, s.url eventUrl, v.name eventContent, v.url eventVideoUrl, v.thumbnail eventPicUrl,null eventComment, null primaryPlayerId, null primaryPlayerName , null primaryPlayerLogo, coalesce(comment_count.count, 0) as commentCount, coalesce(support_count.count, 0) as supportCount, coalesce(share_count.count, 0) as shareCount from share s join share_video sv on sv.`share_id` = s.id join video v on v.id = sv.video_id join football_player fp on fp.id = s.player_id left join (select video_id, count(1) as count from comment_video group by video_id) comment_count on v.id = comment_count.video_id left join (select video_id, count(1) as count from support_video sv join support s on s.id = sv.support_id and s.status = 1 group by video_id) support_count on v.id = support_count.video_id left join (select video_id, count(1) as count from share_video group by video_id) share_count on v.id = share_count.video_id where v.tournament_id = #{tournamentId} and fp.id = #{playerId}
