select fp.id playerId, fp.nick_name playerName, fp.imageUrl playerLogo, s.date eventDate, 'forward' eventAction, 'moment' eventType, m.type eventDetailType, m.id eventId, s.url eventUrl, m.content eventContent, m.url eventVideoUrl, m.url eventPicUrl, null eventComment, (select id from football_player fp where fp.id = m.player_id) primaryPlayerId, (select nick_name from football_player fp where fp.id = m.player_id) primaryPlayerName , (select imageUrl from football_player fp where fp.id = m.player_id) primaryPlayerLogo, coalesce(comment_count.count, 0) as commentCount, coalesce(support_count.count, 0) as supportCount, coalesce(share_count.count, 0) as shareCount from share s join football_player fp on fp.id = s.player_id join share_moment sm on sm.share_id = s.id join moment m on m.id = sm.moment_id and m.player_id != s.player_id left join (select moment_id, count(1) as count from comment_moment group by moment_id) comment_count on m.id = comment_count.moment_id left join (select moment_id, count(1) as count from support_moment sv join support s on s.id = sv.support_id and s.status = 1 group by moment_id) support_count on m.id = support_count.moment_id left join (select moment_id, count(1) as count from share_moment group by moment_id) share_count on m.id = share_count.moment_id where m.tournament_id = #{tournamentId} and fp.id = #{playerId}
