select fp.id playerId, fp.nick_name playerName, fp.imageUrl playerLogo, m.date eventDate, 'share' eventAction, 'moment' eventType, m.type eventDetailType, m.id eventId, m.url eventUrl, m.content eventContent, null eventVideoUrl, m.url eventPicUrl, null eventComment, null primaryPlayerId, null primaryPlayerName , null primaryPlayerLogo, coalesce((select count(1) from comment_moment cm where cm.moment_id = m.id), 0) as commentCount, coalesce((select count(1) from support_moment sm where sm.moment_id = m.id), 0) as supportCount, coalesce((select count(1) from share_moment shm where shm.moment_id = m.id), 0) as shareCount from moment m join football_player fp on fp.id = m.player_id where m.type = 'pic' and m.tournament_id = #{tournamentId} and fp.id = #{playerId}
